import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { 
  FaCalendarAlt, 
  FaClock, 
  FaUsers, 
  FaUtensils, 
  FaCheckCircle, 
  FaEnvelope, 
  FaPhone, 
  FaArrowRight, 
  FaInfoCircle,
  FaUser,
  FaMapMarkerAlt,
  FaInstagram
} from "react-icons/fa";

const ReservationsContainer = styled.div`
  width: 100%;
  padding-top: 80px;
  min-height: 100vh;
`;

const PageHeader = styled.section`
  position: relative;
  height: 40vh;
  min-height: 300px;
  background: linear-gradient(rgba(0, 0, 0, 0.65), rgba(0, 0, 0, 0.65)),
    url("/images/reservations-header.jpg") no-repeat center center/cover;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  color: white;
`;

const PageTitle = styled.h1`
  font-size: 3.5rem;
  margin-bottom: 1rem;
  letter-spacing: 1px;
  font-weight: 600;
`;

const PageSubtitle = styled.p`
  font-size: 1.2rem;
  max-width: 600px;
  margin: 0 auto;
`;

const ReservationsSection = styled.section`
  padding: 5rem 0;
  background-color: ${props => props.theme.colors.lightBg};
  min-height: 70vh;
`;

const Container = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const BookingContainer = styled.div`
  background: white;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 5px 30px rgba(0, 0, 0, 0.08);
  position: relative;
  margin-top: -80px;
`;

const BookingHeader = styled.div`
  padding: 2rem;
  border-bottom: 1px solid #f0f0f0;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const RestaurantTitle = styled.h2`
  font-size: 2rem;
  margin: 0;
  font-weight: 600;
`;

const BookingContent = styled.div`
  padding: 0;
  display: flex;
  flex-direction: column;
`;

const BookingSidebar = styled.div`
  padding: 2rem;
  background-color: #f9f9f9;
  border-top: 1px solid #f0f0f0;
`;

const RestaurantInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-top: 1.5rem;
  font-size: 0.95rem;
  color: #555;
`;

const InfoItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;

  svg {
    color: ${props => props.theme.colors.primary};
    min-width: 18px;
  }
`;

const BookingStep = styled(motion.div)`
  padding: 3rem 2rem;
`;

const StepTitle = styled.h3`
  font-size: 1.75rem;
  margin-bottom: 2rem;
  color: #333;
  font-weight: 500;
`;

const StepContent = styled.div``;

const StepActions = styled.div`
  margin-top: 2rem;
  display: flex;
  justify-content: flex-end;
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;
  margin-bottom: 1.5rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 1rem;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
`;

const FormLabel = styled.label`
  font-size: 0.9rem;
  color: #555;
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 0.5rem;

  svg {
    color: ${props => props.theme.colors.primary};
  }
`;

const FormInput = styled.input`
  padding: 0.9rem;
  border: 1px solid #e1e1e1;
  border-radius: 4px;
  font-size: 1rem;
  transition: all 0.2s ease;

  &:focus {
    border-color: ${props => props.theme.colors.primary};
    outline: none;
    box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.05);
  }
`;

const FormSelect = styled.select`
  padding: 0.9rem;
  border: 1px solid #e1e1e1;
  border-radius: 4px;
  font-size: 1rem;
  transition: all 0.2s ease;
  appearance: none;
  background-image: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e");
  background-repeat: no-repeat;
  background-position: right 1rem center;
  background-size: 1em;

  &:focus {
    border-color: ${props => props.theme.colors.primary};
    outline: none;
    box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.05);
  }
`;

const FormTextarea = styled.textarea`
  padding: 0.9rem;
  border: 1px solid #e1e1e1;
  border-radius: 4px;
  font-size: 1rem;
  min-height: 100px;
  resize: vertical;
  transition: all 0.2s ease;

  &:focus {
    border-color: ${props => props.theme.colors.primary};
    outline: none;
    box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.05);
  }
`;

const Button = styled.button`
  background-color: ${props => props.theme.colors.primary};
  color: white;
  padding: 0.9rem 1.8rem;
  border: none;
  border-radius: 4px;
  font-size: 0.95rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;

  &:hover {
    background-color: ${props => props.theme.colors.primaryDark};
  }

  &:disabled {
    background-color: #cccccc;
    cursor: not-allowed;
  }
`;

const GuestSelector = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
  margin-bottom: 2rem;
`;

const GuestButton = styled.button`
  min-width: 3rem;
